    <?php     class PDF extends CI_Controller    {    function __construct()    {        parent::__construct();		$this->load->library("pdf2");		$this->load->library("fpdf");		$this->load->library("fpdi");		$this->load->library("PDFMerger2"); // masih error			$this->load->model('mm_documents');			$this->load->model(array('mm_categories', 'mm_users'));	    }    //Page header    function Header()    {    //Logo    //$this->Image('logo-ubl.jpg',10,8);    //Arial bold 15    $this->SetFont('Arial','B',15);    //pindah ke posisi ke tengah untuk membuat judul    $this->Cell(80);    //judul    $this->Cell(30,10,'LAPORAN REKAPITULASI PENERIMAAN MAHASISWA BARU',0,0,'C');    //pindah baris    $this->Ln(20);    //buat garis horisontal    $this->Line(10,25,200,25);    }         //Page Content    function Content()    {    $this->SetFont('Times','',12);    for($i=1; $i<=40; $i++)    $this->Cell(0,10,'Laporan Mahasiswa '.$i,0,1);    }         //Page footer    function Footer()    {    //atur posisi 1.5 cm dari bawah    $this->SetY(-15);    //buat garis horizontal    $this->Line(10,$this->GetY(),200,$this->GetY());    //Arial italic 9    $this->SetFont('Arial','I',9);    //nomor halaman    $this->Cell(0,10,'Halaman '.$this->PageNo().' dari {nb}',0,0,'R');    }    }    	$pdf = new PDF();	$filename = dirname($_SERVER['SCRIPT_FILENAME'])."/assets/dokumen_prosedur/merger_31.pdf";	$pageCount = $pdf->setSourceFile($filename);	    //contoh pemanggilan class    $pdf->AliasNbPages();    $pdf->AddPage();    $pdf->Content();    $pdf->Output();    ?>